import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// Sample data for countries, states, and cities
const locationData = {
  "India": {
    "Maharashtra": ["Mumbai", "Pune", "Nagpur", "Nashik", "Aurangabad"],
    "Karnataka": ["Bangalore", "Mysore", "Hubli", "Mangalore", "Belgaum"],
    "Gujarat": ["Ahmedabad", "Surat", "Vadodara", "Rajkot", "Bhavnagar"],
    "Punjab": ["Chandigarh", "Ludhiana", "Amritsar", "Jalandhar", "Patiala"],
    "Uttar Pradesh": ["Lucknow", "Kanpur", "Agra", "Varanasi", "Allahabad"],
    "Tamil Nadu": ["Chennai", "Coimbatore", "Madurai", "Salem", "Trichy"],
    "Rajasthan": ["Jaipur", "Jodhpur", "Udaipur", "Kota", "Ajmer"],
    "Haryana": ["Gurgaon", "Faridabad", "Panipat", "Ambala", "Karnal"]
  },
  "USA": {
    "California": ["Los Angeles", "San Francisco", "San Diego", "Sacramento", "Fresno"],
    "Texas": ["Houston", "Dallas", "Austin", "San Antonio", "Fort Worth"],
    "New York": ["New York City", "Buffalo", "Rochester", "Yonkers", "Syracuse"],
    "Florida": ["Miami", "Orlando", "Tampa", "Jacksonville", "Tallahassee"]
  },
  "Brazil": {
    "São Paulo": ["São Paulo", "Campinas", "São Bernardo", "Santo André", "Osasco"],
    "Rio de Janeiro": ["Rio de Janeiro", "Niterói", "Duque de Caxias", "Nova Iguaçu", "Belford Roxo"],
    "Minas Gerais": ["Belo Horizonte", "Uberlândia", "Contagem", "Juiz de Fora", "Betim"]
  }
};

const SignUpPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [country, setCountry] = useState("");
  const [state, setState] = useState("");
  const [city, setCity] = useState("");
  const [village, setVillage] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [availableStates, setAvailableStates] = useState([]);
  const [availableCities, setAvailableCities] = useState([]);
  const navigate = useNavigate();

  // Update states when country changes
  useEffect(() => {
    if (country && locationData[country]) {
      setAvailableStates(Object.keys(locationData[country]));
      setState("");
      setCity("");
      setAvailableCities([]);
    } else {
      setAvailableStates([]);
      setAvailableCities([]);
    }
  }, [country]);

  // Update cities when state changes
  useEffect(() => {
    if (country && state && locationData[country]?.[state]) {
      setAvailableCities(locationData[country][state]);
      setCity("");
    } else {
      setAvailableCities([]);
    }
  }, [state, country]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert("Passwords do not match");
      return;
    }
    // TODO: Add signup API call
    console.log("Sign up attempted with", { 
      name, 
      email, 
      mobile, 
      country, 
      state, 
      city, 
      village, 
      password 
    });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-yellow-50">
      <form
        onSubmit={handleSubmit}
        className="bg-gray-50 p-8 rounded-lg shadow-lg w-full max-w-sm border-2 border-yellow-900"
      >
        <h2 className="text-3xl font-extrabold mb-6 text-red-900 text-center">
          Sign Up
        </h2>

        <label className="block mb-2 font-semibold text-lime-700">Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 placeholder-yellow-900 focus:ring-lime-700 focus:border-lime-700"
          placeholder="Enter your full name"
        />

        <label className="block mb-2 font-semibold text-lime-700">Email (Optional):</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 placeholder-yellow-900 focus:ring-lime-700 focus:border-lime-700"
          placeholder="Enter your email"
        />

        <label className="block mb-2 font-semibold text-lime-700">Mobile Number:</label>
        <input
          type="tel"
          value={mobile}
          onChange={(e) => setMobile(e.target.value)}
          required
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 placeholder-yellow-900 focus:ring-lime-700 focus:border-lime-700"
          placeholder="Enter your mobile number"
        />

        <label className="block mb-2 font-semibold text-lime-700">Country:</label>
        <select
          value={country}
          onChange={(e) => setCountry(e.target.value)}
          required
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 focus:ring-lime-700 focus:border-lime-700"
        >
          <option value="">Select your country</option>
          {Object.keys(locationData).map(countryName => (
            <option key={countryName} value={countryName}>{countryName}</option>
          ))}
        </select>

        <label className="block mb-2 font-semibold text-lime-700">State:</label>
        <select
          value={state}
          onChange={(e) => setState(e.target.value)}
          required
          disabled={!country}
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 focus:ring-lime-700 focus:border-lime-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <option value="">Select your state</option>
          {availableStates.map(stateName => (
            <option key={stateName} value={stateName}>{stateName}</option>
          ))}
        </select>

        <label className="block mb-2 font-semibold text-lime-700">City:</label>
        <select
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
          disabled={!state}
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 focus:ring-lime-700 focus:border-lime-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <option value="">Select your city</option>
          {availableCities.map(cityName => (
            <option key={cityName} value={cityName}>{cityName}</option>
          ))}
        </select>

        <label className="block mb-2 font-semibold text-lime-700">Village (Optional):</label>
        <input
          type="text"
          value={village}
          onChange={(e) => setVillage(e.target.value)}
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 placeholder-yellow-900 focus:ring-lime-700 focus:border-lime-700"
          placeholder="Enter your village/area"
        />

        <label className="block mb-2 font-semibold text-lime-700">Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className="w-full p-3 mb-4 border border-yellow-400 rounded bg-yellow-50 text-gray-900 placeholder-yellow-900 focus:ring-lime-700 focus:border-lime-700"
          placeholder="Create a password"
        />

        <label className="block mb-2 font-semibold text-lime-700">
          Confirm Password:
        </label>
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
          className="w-full p-3 mb-6 border border-yellow-400 rounded bg-yellow-50 text-gray-900 placeholder-yellow-900 focus:ring-lime-700 focus:border-lime-700"
          placeholder="Confirm your password"
        />

        <button
          type="submit"
          className="w-full bg-lime-600 text-gray-50 py-3 rounded-md font-bold border border-yellow-900 hover:bg-red-900 hover:text-yellow-50 transition duration-200"
        >
          Sign Up
        </button>

        {/* Back Button */}
        <button
          type="button"
          onClick={() => navigate(-1)}
          className="w-full mt-4 bg-gray-200 text-gray-900 py-3 rounded-md font-bold border border-yellow-900 hover:bg-gray-300 transition duration-200"
        >
          Back
        </button>
      </form>
    </div>
  );
};

export default SignUpPage;
